// File: contracts/IRewardDistributionRecipient.sol

import '../interfaces/IRewardDistributionRecipient.sol';

import './LPTokenWrapper.sol';

/**
* @title HUSD-GOCLP Token
* @notice 30,100000,75%
 */
contract HUSDGOCLPTokenSharePool is LPTokenWrapper, IRewardDistributionRecipient {
    IERC20 public GOT;
    /// @notice  = 30
    uint256 public DURATION = 30 days;
    /// @notice 
    uint256 public initreward = 100000 * 10**18;
    /// @notice 
    uint256 public starttime; // starttime TBD
    /// @notice 
    uint256 public periodFinish = 0;
    /// @notice 
    uint256 public rewardRate = 0;
    /// @notice 
    uint256 public lastUpdateTime;
    /// @notice 
    uint256 public rewardPerTokenStored;
    /// @notice Token
    mapping(address => uint256) public userRewardPerTokenPaid;
    /// @notice 
    mapping(address => uint256) public rewards;

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);

    /**
     * @dev 
     * @param GOT_ GOT
     * @param lptoken_ LPtoken
     * @param starttime_ 
     */
    constructor(
        address GOT_,
        address lptoken_,
        uint256 starttime_
    ) public {
        GOT = IERC20(GOT_);
        lpt = IERC20(lptoken_);
        starttime = starttime_;
    }

    /**
     * @dev 
     */
    modifier checkStart() {
        require(block.timestamp >= starttime, 'LPTokenSharePool: not start');
        _;
    }

    /**
     * @dev 
     */
    modifier checkhalve() {
        // >=
        if (block.timestamp >= periodFinish) {
            //  =  * 75%
            initreward = initreward.mul(75).div(100);
            //  =  / 30
            rewardRate = initreward.div(DURATION);
            //  =  + 30
            periodFinish = block.timestamp.add(DURATION);
            // 
            emit RewardAdded(initreward);
        }
        _;
    }

    /**
     * @dev 
     * @param account 
     */
    modifier updateReward(address account) {
        //  = Token
        rewardPerTokenStored = rewardPerToken();
        //  = min(,)
        lastUpdateTime = lastTimeRewardApplicable();
        // !=0
        if (account != address(0)) {
            //  = 
            rewards[account] = earned(account);
            // Token = 
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /**
     * @dev 
     * @return 
     * @notice ,
     */
    function lastTimeRewardApplicable() public view returns (uint256) {
        // (,)
        return Math.min(block.timestamp, periodFinish);
    }

    /**
     * @dev Token
     * @return 
     */
    function rewardPerToken() public view returns (uint256) {
        // 0
        if (totalSupply() == 0) {
            return rewardPerTokenStored;
        }
        //  + (min(,) - ) *  * 1e18 / 
        return
            rewardPerTokenStored.add(
                lastTimeRewardApplicable().sub(lastUpdateTime).mul(rewardRate).mul(1e18).div(totalSupply())
            );
    }

    /**
     * @dev 
     * @param account 
     */
    function earned(address account) public view returns (uint256) {
        //  * (Token - Token) / 1e18 + 
        return
            balanceOf(account).mul(rewardPerToken().sub(userRewardPerTokenPaid[account])).div(1e18).add(
                rewards[account]
            );
    }

    /**
     * @dev token
     * @param amount 
     */
    // stake visibility is public as overriding LPTokenWrapper's stake() function
    function stake(uint256 amount) public override updateReward(msg.sender) checkhalve checkStart {
        // >0
        require(amount > 0, 'HUSDGOCLPTokenSharePool: Cannot stake 0');
        // 
        super.stake(amount);
        // 
        emit Staked(msg.sender, amount);
    }

    /**
     * @dev token
     * @param amount 
     */
    function withdraw(uint256 amount) public override updateReward(msg.sender) checkhalve checkStart {
        // >0
        require(amount > 0, 'HUSDGOCLPTokenSharePool: Cannot withdraw 0');
        // 
        super.withdraw(amount);
        // 
        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @dev 
     */
    function exit() external {
        // 
        withdraw(balanceOf(msg.sender));
        // 
        getReward();
    }

    /**
     * @dev 
     */
    function getReward() public updateReward(msg.sender) checkhalve checkStart {
        //  = 
        uint256 reward = earned(msg.sender);
        // >0
        if (reward > 0) {
            //  = 0
            rewards[msg.sender] = 0;
            // 
            GOT.safeTransfer(msg.sender, reward);
            // 
            emit RewardPaid(msg.sender, reward);
        }
    }

    /**
     * @dev 
     * @param reward 
     */
    function notifyRewardAmount(uint256 reward) external override onlyRewardDistribution updateReward(address(0)) {
        // >
        if (block.timestamp > starttime) {
            //  >= 
            if (block.timestamp >= periodFinish) {
                //  =  / 30
                rewardRate = reward.div(DURATION);
            } else {
                //  =  - 
                uint256 remaining = periodFinish.sub(block.timestamp);
                //  =  *  (0)
                uint256 leftover = remaining.mul(rewardRate);
                //  = ( + ) / 30
                rewardRate = reward.add(leftover).div(DURATION);
            }
            // = 
            lastUpdateTime = block.timestamp;
            //  =  + 30
            periodFinish = block.timestamp.add(DURATION);
            // 
            emit RewardAdded(reward);
        } else {
            //  =  / 30
            rewardRate = initreward.div(DURATION);
            //  = 
            lastUpdateTime = starttime;
            //  =  + 30
            periodFinish = starttime.add(DURATION);
            // 
            emit RewardAdded(reward);
        }
    }
}
