// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import '@openzeppelin/contracts/math/SafeMath.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

import '../interfaces/IRewardDistributionRecipient.sol';

interface IDistributor {
    function distribute() external;
}

contract InitialGOTDistributor is IDistributor {
    using SafeMath for uint256;

    event Distributed(address pool, uint256 cashAmount);
    /// @notice 只能运行一次
    bool public once = true;

    /// @notice GOT地址
    IERC20 public GOT;
    /// @notice HUSDGOCLPPool 矿池地址
    IRewardDistributionRecipient public HUSDGOCLPPool;
    /// @notice HUSDGOC 初始奖励
    uint256 public HUSDGOCInitialBalance;
    /// @notice HUSDGOTLPPool 矿池地址
    IRewardDistributionRecipient public HUSDGOTLPPool;
    /// @notice HUSDGOT 初始奖励
    uint256 public HUSDGOTInitialBalance;

    /**
    * @dev 构造函数
    * @param _GOT GOT地址
    * @param _HUSDGOCLPPool HUSDGOCLPPool 矿池地址
    * @param _HUSDGOCInitialBalance HUSDGOC 初始奖励
    * @param _HUSDGOTLPPool HUSDGOTLPPool 矿池地址
    * @param _HUSDGOTInitialBalance HUSDGOT 初始奖励
     */
    constructor(
        IERC20 _GOT,
        IRewardDistributionRecipient _HUSDGOCLPPool,
        uint256 _HUSDGOCInitialBalance,
        IRewardDistributionRecipient _HUSDGOTLPPool,
        uint256 _HUSDGOTInitialBalance
    ) public {
        GOT = _GOT;
        HUSDGOCLPPool = _HUSDGOCLPPool;
        HUSDGOCInitialBalance = _HUSDGOCInitialBalance;
        HUSDGOTLPPool = _HUSDGOTLPPool;
        HUSDGOTInitialBalance = _HUSDGOTInitialBalance;
    }

    /**
    * @dev 分发奖励
     */
    function distribute() public override {
        require(
            once,
            'InitialGOTDistributor: you cannot run this function twice'
        );
        // 将奖励的GOT发送给HUSDGOCLPPool 矿池地址
        GOT.transfer(address(HUSDGOCLPPool), HUSDGOCInitialBalance);
        // 通知奖励
        HUSDGOCLPPool.notifyRewardAmount(HUSDGOCInitialBalance);
        // 触发分发事件
        emit Distributed(address(HUSDGOCLPPool), HUSDGOCInitialBalance);

        // 将奖励的GOT发送给HUSDGOTLPPool 矿池地址
        GOT.transfer(address(HUSDGOTLPPool), HUSDGOTInitialBalance);
        // 通知奖励
        HUSDGOTLPPool.notifyRewardAmount(HUSDGOTInitialBalance);
        // 触发分发事件
        emit Distributed(address(HUSDGOTLPPool), HUSDGOTInitialBalance);

        once = false;
    }
}

